!function(){tinymce.PluginManager.add("sr_tinymce_button",function(e,t){e.addButton("sr_tinymce_button",{title:"Insert Reviews",icon:"icon sr-icon",type:"button",onclick:function(){e.windowManager.open({title:"Insert Reviews",body:[{type:"textbox",name:"title",label:"Title"},{type:"listbox",name:"columns",label:"Columns",values:[{text:"1",value:"1"},{text:"2",value:"2"},{text:"3",value:"3"}]},{type:"textbox",name:"number",label:"Number of reviews"},{type:"listbox",name:"scope",label:"Scope",values:[{text:"Recent reviews",value:"recent"},{text:"Reviews of a Specific product",value:"specific-product"},{text:"Specific reviews",value:"specific-reviews"}]},{type:"textbox",name:"product_id",label:"Product ID"},{type:"textbox",name:"review_ids",label:"Review IDs"},{type:"listbox",name:"layout",label:"Style",values:[{text:"Plain",value:"style-1"},{text:"Bubble",value:"srtle-2"},{text:"Graphic",value:"srtle-3"}]},{type:"checkbox",name:"carousel",label:"Display reviews in a carousel"},{type:"checkbox",name:"gravatar",label:"Display the reviewers Gravatar"}],onsubmit:function(t){e.insertContent('[storefront_reviews title="'+t.data.title+'" columns="'+t.data.columns+'" number="'+t.data.number+'" scope="'+t.data.scope+'" product_id="'+t.data.product_id+'" review_ids="'+t.data.review_ids+'" layout="'+t.data.layout+'" carousel="'+t.data.carousel+'" gravatar="'+t.data.gravatar+'"]')}})}})})}();